var finabel = require("./finabel");
var assert = require("assert");

const complain = console.error;
const print = console.log;
const crlf = () => print();

var tests = 0;
var errors = 0;

function report(useCase, expected, key, salt, rounds) {
  ++tests;
  useCase += " == " + expected + " :";
  try {
    assert.equal(finabel(key, salt, rounds), expected);
    print(useCase, "passed");
  } catch (error) {
    ++errors;
    complain(useCase, "FAILED");
  }
  crlf();
}

crlf();
print("****************** Unit Test ******************");
crlf();

report(
  "finabel()",
  ""
);

report(
  "finabel('foobar')",
  "",
  "foobar"
);

report(
  "finabel('foo', 'bar')",
  "",
  "foo",
  "bar"
);

report(
  "finabel('bar', 'foo')",
  "",
  "bar",
  "foo"
);

report(
  "finabel('foo', 'bar', 8192)",
  "",
  "foo",
  "bar",
  8192
);

report(
  "finabel('foo', 'bar', 4096)",
  "",
  "foo",
  "bar",
  4096
);

report(
  "finabel('😃🧘🏻‍♂️🌍🍞🚗', '📞🎉♥️🏁')",
  "",
  "😃🧘🏻‍♂️🌍🍞🚗",
  "📞🎉♥️🏁"
);

const EXIT_SUCCESS = 0;
const EXIT_FAILURE = 1;

var code = undefined;

if (errors) {
  complain(" Passed", tests - errors, "of", tests, "tests.");
  code = EXIT_FAILURE;
} else {
  print(" All tests passed.");
  code = EXIT_SUCCESS;
}

crlf();
print("***********************************************");
crlf();

process.exit(code);
